let operator = '';
let firstNumber = 0;
let numTemp = 0;
function calculate(value ,buttonName){
    if(buttonName ==="="){
        //calculate
        firstNumber = firstNumber + numTemp; //switch(operator)
        //display
    }
    else if(buttonName === "1"){
        numTemp= numTemp*10 + 1;
        }
    else {
        switch(buttonName){
            case "+":
                //plus
                break;
            case "-":
                //moin
                break;
            case "*":
                //mult
                break;
            case "/":
                //div
                break;
            default: break;
        }
    }
    firstNumber = value;
    operator = buttonName;

    console.log(firstNumber+ "in calculate  "+ buttonName);
}

export default calculate;
