let operator = '';
let firstNumber = 0;
let secondNumber = 0;
function calculate(value,buttonName, type){

    secondNumber = value;
    if(type === "number"){

        if(buttonName >=0 || buttonName < 10 ){
            secondNumber= secondNumber*10 + buttonName;console.log("we got one");
            return secondNumber;
        }
    }
    
    else if(buttonName === "+" || buttonName ==="-" || buttonName==="*" || buttonName ==="/" || buttonName==='='){
        operator = buttonName;
        switch(operator){
            case "+":
                firstNumber= firstNumber + secondNumber;
                secondNumber=0;
                break;
                case "-":
                firstNumber= firstNumber - secondNumber;
                secondNumber=0;
                break;
                case "*":
                    firstNumber= firstNumber * secondNumber;
                    secondNumber=0;
                    break;
                    case "/":
                        firstNumber= firstNumber / secondNumber;
                        secondNumber=0;
                break;
            default: break;
        }
        if(buttonName ==="="){
           firstNumber = firstNumber + secondNumber;
           return firstNumber;
        }
    }
    
    return secondNumber;

}

export default calculate;
