let operator = '';
let firstNumber = 0;
let secondNumber = 0;
function calculate(value,buttonName){
    
    
    if(buttonName === "1"){
        secondNumber= secondNumber*10 + 1;
    }
    
    else if(buttonName === "+" || buttonName ==="-" || buttonName==="*" || buttonName ==="/" || buttonName==='='){
        operator = buttonName;
        switch(operator){
            case "+":
                firstNumber+=secondNumber;
                break;
                case "-":
                firstNumber-=secondNumber;
                break;
                case "*":
                firstNumber*=secondNumber;
                break;
                case "/":
                firstNumber/=secondNumber;
                break;
            default: break;
        }
        if(buttonName ==="="){
           firstNumber += secondNumber;
        }
    }
    
    return firstNumber;

}

export default calculate;
