import logo from './logo.svg';
import './App.css';
import Display from './Display.jsx';
import ButtonsPanel from './ButtonsPanel.jsx';
import { useState } from 'react';
import Button from './Button';
import calculate from '../logic/logic.js';

function App() {



  const[value,setValue]= useState(0);


  const handleClick= (buttonName) => {
    setValue(value + 1); console.log('before');
    calculate(value, buttonName);console.log("after");console.log(value)
  }
  return (
    <div className="App">
     <Display value = {value} setValue = {setValue}/>,
     <ButtonsPanel value = {value} setValue = {setValue} handleClick = {handleClick} />
    </div>
  );

}
export default App;
